INSERT INTO `ygay_info` VALUES ('16', '46', '-1', '', '<p style=\"text-transform:none;background-color:#ffffff;text-indent:0px;margin:0px 0px 6px;letter-spacing:normal;font:14px/1.8em \'WenQuanYi Micro Hei Mono\', \'WenQuanYi Micro Hei\', \'Microsoft Yahei Mono\', \'Microsoft Yahei\', sans-serif;white-space:normal;color:#696969;word-spacing:0px;-webkit-text-stroke-width:0px;\" class=\"MsoNormal\">\r\n	<b>\r\n	<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n		烟台良源母婴护理服务有限公司\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		&nbsp;\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:36pt;\">\r\n		烟台良源母婴护理服务有限公司成立于2014年，注册资金300万，旗下拥有线上母婴供需平台“阳光阿姨”，“阳光阿姨”是烟威地区服务品质最专业、品牌化程度最完善的一站式母婴护理网络服务平台。本公司依托滨州医学院强大的师资团队，为追求生活品质的现代家庭提供：高级育婴师、公共营养师、养老陪护、催乳师、高级月嫂、专业培训、健康咨询等高品质服务。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:36pt;\">\r\n		“阳光阿姨”拥有地区内最优秀、最专业、最富有爱心与诚心的月嫂200余名，是烟威地区首家拥有护理专业大学生月嫂的品牌服务机构，全国首家实施高校护理专业月嫂培训机构。采用自主招生、自主培养、自主导向、自主监督、自主服务的五位一体运营模式。“阳光阿姨”一直满怀着对新生命的呵护与关爱，用我们的心去培训服务，因为高品质的专业服务是我们的生命，也是每个家庭对我们信任与尊重的基础\r\n	</p>\r\n	<p class=\"MsoNormal\">\r\n		&nbsp;&nbsp;&nbsp;&nbsp;公司以国内家庭护理行业为服务市场，始终坚持以客户的实际需求为第一的服务宗旨。提倡“以人为本”的服务新理念，为家庭提供尽善尽美的和谐生活，解决日益受社会重视的孕婴护理问题。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:28.1pt;\">\r\n		公司理念：价值、专业、健康、感恩、使命。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:28.1pt;\">\r\n		公司口号：源于医学，服务家庭，我们更专业！\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:36pt;\">\r\n		公司愿景：“有阳光阿姨的地方就有阳光”<b style=\"line-height:1.8em;text-indent:0px;\"></b>\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:36pt;display:inline !important;\">\r\n		招聘职位：推广讲师5名\r\n	</p>\r\n</b>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"margin-left:0pt;text-indent:36pt;display:inline !important;\">\r\n	市场专员10名\r\n</p>\r\n<p style=\"text-transform:none;background-color:#ffffff;text-indent:0px;margin:0px 0px 6px;letter-spacing:normal;font:14px/1.8em \'WenQuanYi Micro Hei Mono\', \'WenQuanYi Micr