INSERT INTO `ygay_infolist` VALUES ('85', '1', '54', '0', '0,', '-1', '-1', '', '“90后” 小伙当专业“奶爸', '', '', 'h,c,a', '', 'hannuo', '', '', '', '<div style=\"text-align:center;padding-bottom:0px;line-height:normal;background-color:#f7faff;margin:0px auto;padding-left:0px;width:590px;padding-right:0px;font-family:Simsun;white-space:normal;color:#391c09;padding-top:0px;\" class=\"nxx\">\r\n	<div style=\"padding-bottom:15px;margin:0px auto;padding-left:0px;padding-right:0px;font-size:26px;font-weight:bold;padding-top:15px;\" class=\"zb\">\r\n		<span style=\"font-size:32px;\"><span style=\"line-height:2;\"><span style=\"line-height:2.5;\"></span></span>90后小伙要当专业“奶爸”</span> \r\n	</div>\r\n	<div style=\"padding-bottom:10px;margin:0px auto;padding-left:10px;width:580px;padding-right:10px;padding-top:10px;\" class=\"fbt\">\r\n		<span style=\"margin:0px auto;font-size:16px;\">创母婴服务公司,立志将“看孩子”做成事业</span> \r\n	</div>\r\n<span style=\"font-size:16px;\">2015年06月10日　　来源：</span><a style=\"margin:0px auto;color:#83006f;text-decoration:none;\" href=\"http://www.qlwb.com.cn/\" target=\"_blank\"><span style=\"font-size:16px;\">齐鲁晚报</span></a> \r\n</div>\r\n<div style=\"line-height:normal;background-color:#f7faff;margin:0px auto;width:600px;font-family:Simsun;white-space:normal;height:50px;color:#391c09;\" class=\"dh\">\r\n	<div style=\"padding-bottom:0px;margin:0px auto;padding-left:10px;width:400px;padding-right:0px;float:left;height:30px;padding-top:20px;\" class=\"wx\">\r\n		<div style=\"margin:0px auto;zoom:1;\" class=\"bdsharebuttonbox bdshare-button-style0-16\" data-bd-bind=\"1434510107544\">\r\n			<a style=\"background-image:url(http://bdimg.share.baidu.com/static/api/img/share/icons_0_16.png?v=d754dcc0.png);line-height:16px;margin:6px 6px 6px 0px;padding-left:17px;background-repeat:no-repeat;background-position:0px -52px;float:left;height:16px;color:#83006f;cursor:pointer;text-decoration:none;\" class=\"bds_qzone\" title=\"分享到QQ空间\" href=\"http://sjb.qlwb.com.cn/qlwb/content/2015